<div class="update-product-container">
    <h2 class="update-product-title">Update Product</h2>

    <form [formGroup]="updateProductForm" (ngSubmit)="onUpdate()" class="update-product-form">
        <div class="form-group">
            <label>Name:</label>
            <input type="text" formControlName="name" class="form-input" />
            <div class="error-message" *ngIf="updateProductForm.get('name')?.invalid && updateProductForm.get('name')?.touched">
                <small *ngIf="updateProductForm.get('name')?.errors?.['required']">Product name is required.</small>
                <small *ngIf="updateProductForm.get('name')?.errors?.['minlength']">Name must be at least 3 characters long.</small>
            </div>
        </div>

        <div class="form-group">
            <label>Description:</label>
            <input type="text" formControlName="description" class="form-input" />
            <div class="error-message" *ngIf="updateProductForm.get('description')?.invalid && updateProductForm.get('description')?.touched">
                <small *ngIf="updateProductForm.get('description')?.errors?.['required']">Description is required.</small>
            </div>
        </div>

        <div class="form-group">
            <label>Manufacturer:</label>
            <input type="text" formControlName="manufacturer" class="form-input" />
            <div class="error-message" *ngIf="updateProductForm.get('manufacturer')?.invalid && updateProductForm.get('manufacturer')?.touched">
                <small *ngIf="updateProductForm.get('manufacturer')?.errors?.['required']">Manufacturer is required.</small>
            </div>
        </div>

        <div class="form-group">
            <label>Price:</label>
            <input type="number" formControlName="price" class="form-input" />
            <div class="error-message" *ngIf="updateProductForm.get('price')?.invalid && updateProductForm.get('price')?.touched">
                <small *ngIf="updateProductForm.get('price')?.errors?.['required']">Price is required.</small>
                <small *ngIf="updateProductForm.get('price')?.errors?.['min']">Price cannot be negative.</small>
            </div>
        </div>

        <div class="form-group">
            <label>Quantity:</label>
            <input type="number" formControlName="quantity" class="form-input" />
            <div class="error-message" *ngIf="updateProductForm.get('quantity')?.invalid && updateProductForm.get('quantity')?.touched">
                <small *ngIf="updateProductForm.get('quantity')?.errors?.['required']">Quantity is required.</small>
                <small *ngIf="updateProductForm.get('quantity')?.errors?.['min']">Quantity must be at least 1.</small>
            </div>
        </div>

        <button type="submit" [disabled]="updateProductForm.invalid" class="update-button">Update Product</button>
    </form>
</div>
